mapper.select:
SELECT site_id, material_id, supply_id, operate_date,
sl_kg,dj,yfdj, zje,type as detail_type FROM `hte_kc_material_all`
where operate_date between #{fromDate} and #{toDate}

select.foreach:
SELECT sum(sl_kg) as slKg,#{item.id} as id from hte_kc_material_all where site_id=#{item.siteId} and
material_id =
#{item.materialId}
and id <= #{item.id}
